
const fs = require('fs');
const path = require('path');

function search() {
	//console.log(__dirname);
	var package_path = __dirname.split('node_modules')[0];
	var file_list= fs.readdirSync(package_path);
	var idl_list = [];
	//console.log(file_list);
	file_list.forEach(function (file) {
		if (file.includes('.idl') == true) {
			file = path.join(package_path, file);
			idl_list.push(file);
		}
	});	
	//console.log(idl_list);
	return idl_list;
}

module.exports = {
  search: search
};
